/* Modal Styles */
.threejs-modal {
  display: none;
  position: fixed;
  z-index: 2;
  left: 0;
  top: 0;
  width: 100%;
  height: 100%;
  overflow: auto;
  background-color: rgba(0, 0, 0, 0.5);
}

.threejs-modal-content {
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  background: linear-gradient(to right, #ffde5d, var(--yellow-primary-color));
  padding: 20px;
  border: 1px solid #888;
  border-radius: 10px;
  max-width: 80%;
  max-height: 80%;
  overflow: auto;
  text-align: left;
  direction: ltr;
}

.threejs-modal-content__upper {
  display: flex;
  justify-content: space-evenly;
  align-items: center;
}

.threejs-modal-content h2 {
  text-align: center;
}

.threejs-modal-content img {
  max-width: 100%;
  width: 150px;
  height: auto;
  border-radius: 50%;
}

.threejs-modal-content-text {
  background: linear-gradient(to right, rgb(140, 23, 113), rgb(70, 17, 83));
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  margin: 3rem auto;
}

.threejs-close {
  color: #aaa;
  float: right;
  font-size: 28px;
  font-weight: bold;
}

.threejs-close:hover,
.threejs-close:focus {
  color: black;
  text-decoration: none;
  cursor: pointer;
}

#threejs-btn {
  background-color: var(--purple-primary-color);
  white-space: nowrap;
  overflow: hidden;
  text-overflow: ellipsis;
  text-decoration: none;
  color: white;
  padding: 10px 20px;
  border: none;
  border-radius: 5px;
  cursor: pointer;
  display: block;
  text-align: center;
  width: 100%;
  margin: 0 auto;
  transition: 0.2s all ease-in;
}

#threejs-btn:hover {
  background-color: var(--blue-light-color);
  color: var(--purple-primary-color);
}

/* Show modal */
.modal.show {
  display: block;
}

/* Styles for the fixed trigger modal */
#fixed-trigger-modal {
  display: none;
  padding: 20px 5px;
  position: fixed;
  top: 34%;
  right: 0;
  width: 100px;
  height: 140px;
  background-color: var(--purple-primary-color);
  border-radius: 10px 0px 0px 10px;
  cursor: pointer;
  z-index: 9999;
  transition: 0.2s all ease-in;
}

#fixed-trigger-modal:hover {
  background: var(--blue-light-color);
}

#fixed-trigger-modal::before {
  content: "3D Portfolio";
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  color: white;
  font-size: 14px;
  text-align: center;
}

#fixed-trigger-modal:hover::before {
  color: var(--purple-primary-color);
}

.gradient-3d-text {
  background: linear-gradient(
    to right,
    var(--dark-primary-color),
    var(--purple-primary-color)
  );
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
}

/*⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜*/
/*═════════════════════╣ End of Resume ╠═════════════════════*/
/*⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜*/

/*⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜*/
/*═════════════════════╣ Start of Entrepreneur ╠═════════════════════*/
/*⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜*/

#entrepreneur-quotes {
  background: rgba(168, 198, 222, 0.2);
  border-radius: 15px 300px;
  padding: 3rem 0;
  margin: 2rem 5rem;
}

#entrepreneur-quotes-container {
  width: 100%;
  display: flex;
  justify-content: space-between;
  position: relative;
}

#entrepreneur-quotes-container .cards {
  width: 265px;
  height: 400px;
  position: relative;
  background: #000;
  display: flex;
  justify-content: center;
  align-items: center;
  border-radius: 10%;
  margin: 40px auto;
  transform: translateY(25%);
  opacity: 0;
  transition: opacity 500ms ease-in, transform 400ms ease-in;
}

#entrepreneur-quotes-container .cards.appear {
  transform: translateY(0);
  opacity: 1;
  transition: transform 300ms ease-in-out;
}

#entrepreneur-quotes-container .cards.appear:hover {
  transform: translateX(4px) skew(0.5deg, -0.4deg);
}

#entrepreneur-quotes-container .cards::before {
  content: "";
  width: 50%;
  height: 100%;
  left: 0;
  top: 0;
  position: absolute;
  pointer-events: none;
}

.quotes-test:nth-child(2) .cards,
.quotes-test:nth-child(2) .cards .overlay {
  background: linear-gradient(45deg, #3503ad, #f7308c);
  box-shadow: 0px 0px 7px 4px rgba(0, 0, 0, 0.5);
}

.quotes-test:nth-child(3) .cards,
.quotes-test:nth-child(3) .cards .overlay {
  background: linear-gradient(45deg, #ccff00, #09afff);
  box-shadow: 0px 0px 15px 7px rgba(0, 0, 0, 0.8);
}

.quotes-test:nth-child(4) .cards,
.quotes-test:nth-child(4) .cards .overlay {
  background: linear-gradient(45deg, #e91e63, #ffeb3b);
  box-shadow: 0px 0px 7px 4px rgba(0, 0, 0, 0.5);
}

#entrepreneur-quotes-container .cards .imgBx {
  width: 100%;
  height: 100%;
  display: flex;
  justify-content: center;
  align-items: center;
  border-radius: 10%;
  overflow: hidden;
  position: absolute;
  transform: skew(10px, 10px);
}

.quotes-test h3 {
  padding: 0;
  letter-spacing: 2px;
  font-weight: 700;
  text-transform: uppercase;
  font-size: 2rem;
  text-align: center;
  -webkit-text-stroke: 1px black;
  color: var(--yellow-primary-color);
  font-family: Cursive;
  text-shadow: 4px 4px 5px #d4af37;
}

#entrepreneur-quotes-container .cards .overlay {
  width: 50px;
  height: 50px;
  display: flex;
  position: absolute;
  right: 20px;
  bottom: 20px;
  border-radius: 50%;
  transition: 0.5s;
  cursor: pointer;
  box-shadow: 0 2px 5px rgba(0, 0, 0, 0.2);
}

#entrepreneur-quotes-container .cards .overlay:hover {
  width: 100%;
  height: 100%;
  right: 0;
  bottom: 0;
  box-shadow: none;
  opacity: 0.9;
  border-radius: 10%;
}

#entrepreneur-quotes-container .cards .overlay::before {
  content: "بخوان";
  left: 0;
  right: 0;
  top: 0;
  bottom: 0;
  font-size: 12px;
  letter-spacing: 1px;
  font-weight: 500;
  color: #fff;
  position: absolute;
  display: flex;
  justify-content: center;
  align-items: center;
  transition-delay: 0.5s;
}

#entrepreneur-quotes-container .cards .overlay:hover::before {
  display: none;
  transition-delay: 0.5s;
}

#entrepreneur-quotes-container .cards .content {
  padding: 20px;
  box-sizing: border-box;
  color: #fff;
  z-index: 1;
  pointer-events: none;
  opacity: 0;
  visibility: hidden;
  transition: 0s;
  text-align: center;
  line-height: 2;
  font-size: 1.7rem;
}

#entrepreneur-quotes-container .cards .overlay:hover ~ .content {
  opacity: 1;
  visibility: visible;
  transition: 0.2s;
  transition-delay: 0.5s;
}

.imgBx img {
  width: 100%;
  height: 100%;
  object-fit: cover;
  border-radius: 10%;
}

@media (max-width: 980px) {
  #entrepreneur-quotes {
    border-radius: 15px 150px;
  }
}

@media (max-width: 500px) {
  #entrepreneur-quotes {
    border-radius: 15px 75px;
  }

  #Skills {
    border-radius: 100px 15px;
  }

  .threejs-modal-content__upper h2 {
    font-size: 1.7rem;
  }
}

/*⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜*/
/*═════════════════════╣ End of Entrepreneur ╠═════════════════════*/
/*⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜*/

/*⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜*/
/*═════════════════════╣ Start of Contact ╠═════════════════════*/
/*⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜*/

#Contact {
  /* background: #fff; */
  width: 100%;
  height: auto;
  margin: 0 auto 50px;
}

.Contact-info {
  width: 100%;
  display: flex;
  flex-wrap: wrap;
  justify-content: center;
  margin: 60px auto 70px;
}

.cards-Contact {
  position: relative;
  flex: 1;
  max-width: 300px;
  height: 140px;
  background-color: var(--purple-primary-color);
  justify-content: center;
  align-items: center;
  cursor: pointer;
  margin: 20px;
  display: flex;
  border-radius: 5px;
  opacity: 0;
  transition: opacity 400ms ease-in, transform 500ms ease-in;
  transform: translateY(-50px);
}

.cards-Contact.appear {
  transform: translate(0);
  opacity: 1;
}

.icon-Contact {
  font-size: 3rem;
  color: var(--blue-light-color);
  transition: 0.3s linear;
}

.cards-Contact:hover .icon-Contact {
  transform: scale(4);
  opacity: 0;
}

.card-content h3,
.card-content span {
  position: absolute;
  left: 50%;
  transform: translate(-50%);
  font-size: 1.4rem;
  direction: ltr;
  opacity: 0;
}

.card-content h3 {
  color: var(--blue-light-color);
  top: 0;
}

.card-content span {
  bottom: 10px;
  color: var(--yellow-primary-color);
  font-weight: 500;
}

.cards-Contact:hover h3 {
  opacity: 1;
  top: 25px;
  transition: 0.3s linear 0.3s;
}

.cards-Contact:hover span {
  opacity: 1;
  bottom: 45px;
  transition: 0.3s linear 0.3s;
}

/*Down Contact*/
.Down-Contact {
  height: 100%;
  width: 100%;
  display: flex;
  place-items: center;
  place-content: center;
  margin: 20px auto;
}

.Down-Contact .Down-Contact-Button {
  display: inline-block;
  width: 60px;
  height: 60px;
  margin: 0 30px;
  overflow: hidden;
  background-color: var(--yellow-primary-color);
  border-radius: 50px;
  cursor: pointer;
  box-shadow: 0 10px 10px rgba(0, 0, 0, 0.1);
  transition: all 0.3s ease-out, opacity 300ms ease-out,
    transform 400ms ease-out;
  direction: ltr;
  opacity: 0;
}

.Down-Contact .Down-Contact-Button a {
  text-decoration: none;
  color: #000;
}

.Down-Contact .Down-Contact-Button:nth-child(odd) {
  transform: translateY(-20%);
}

.Down-Contact .Down-Contact-Button:nth-child(even) {
  transform: translateY(20%);
}

.Down-Contact .Down-Contact-Button.appear {
  transform: translate(0);
  opacity: 1;
}

.Down-Contact .Down-Contact-Button:hover {
  width: 200px;
}

.Down-Contact .Down-Contact-Button .Down-Contact-Icon {
  display: inline-block;
  height: 60px;
  width: 60px;
  border-radius: 50px;
  box-sizing: border-box;
  line-height: 60px;
  text-align: center;
  transition: 0.3s all ease-out;
}

.Down-Contact .Down-Contact-Button:nth-child(1):hover .Down-Contact-Icon {
  background: rgb(255, 0, 191);
}

.Down-Contact .Down-Contact-Button:nth-child(2):hover .Down-Contact-Icon {
  background: rgb(86, 154, 243);
}

.Down-Contact .Down-Contact-Button:nth-child(3):hover .Down-Contact-Icon {
  background: rgb(255, 123, 0);
}

.Down-Contact .Down-Contact-Button:nth-child(4):hover .Down-Contact-Icon {
  background: rgb(0, 0, 204);
}

.Down-Contact .Down-Contact-Button:nth-child(5):hover .Down-Contact-Icon {
  background: rgb(37, 211, 102);
}

.Down-Contact .Down-Contact-Button:nth-child(6):hover .Down-Contact-Icon {
  background: #0088cc;
}

.Down-Contact .Down-Contact-Button:nth-child(7):hover .Down-Contact-Icon {
  background: #316ff6;
}

.Down-Contact .Down-Contact-Button .Down-Contact-Icon i {
  font-size: 3rem;
  line-height: 60px;
}

.Down-Contact .Down-Contact-Button:hover .Down-Contact-Icon i {
  color: #fff !important;
}

.Down-Contact .Down-Contact-Button span {
  font-size: 2.5rem;
  line-height: 60px;
  margin-left: 10px;
  font-weight: 500;
  transition: all 0.3s ease-out;
}

.Down-Contact .Down-Contact-Button:nth-child(1) span {
  color: rgb(255, 0, 191);
}

.Down-Contact .Down-Contact-Button:nth-child(2) span {
  color: rgb(86, 154, 243);
}

.Down-Contact .Down-Contact-Button:nth-child(3) span {
  color: rgb(255, 123, 0);
}

.Down-Contact .Down-Contact-Button:nth-child(4) span {
  color: rgb(0, 0, 204);
}

.Down-Contact .Down-Contact-Button:nth-child(5) span {
  color: rgb(37, 211, 102);
}

.Down-Contact .Down-Contact-Button:nth-child(6) span {
  color: #0088cc;
}

.Down-Contact .Down-Contact-Button:nth-child(7) span {
  color: #316ff6;
}

/*the text*/
#Contact p {
  text-align: center;
  font-size: 1.7rem;
  font-weight: 500;
  margin: 80px auto 47px;
  max-width: 83%;
  opacity: 0;
  transform: translateY(-20%);
  transition: opacity 350ms ease, transform 300ms ease;
}

#Contact p.appear {
  opacity: 1;
  transform: translateY(0);
}

@media (max-width: 992px) {
  .cards-Contact {
    flex: 100%;
    max-width: 500px;
  }

  .Contact-info {
    margin: 0 auto;
  }

  .LastChild-Cards-Contact {
    margin-bottom: 20px;
  }

  #AboutMe-image img:nth-child(1),
  #AboutMe-image img:nth-child(2) {
    width: 100%;
    height: auto;
    object-fit: contain;
  }

  #AboutMe-image img:nth-child(1) {
    margin-top: 160px;
    animation: float-md-1 5s ease-in-out infinite;
  }

  #AboutMe-image img:nth-child(2) {
    display: block;
    margin: 50px auto;
    animation: float-md-2 5s ease-in-out infinite;
  }
}

@media (max-width: 310px) {
  .Contact-info:nth-child(1) span {
    font-size: 1.2rem;
  }

  .icons {
    display: flex;
    flex-direction: column;
  }
}

@media (max-width: 520px) {
  .Down-Contact {
    display: grid;
  }

  .Down-Contact .Down-Contact-Button {
    margin: 5px auto;
  }
}

/*⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜*/
/*═════════════════════╣ End of Contact ╠═════════════════════*/
/*⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜*/

/*⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜*/
/*═════════════════════╣ Start of Comment ╠═════════════════════*/
/*⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜*/

#Comment {
  background: var(--purple-primary-color);
  padding: 5rem 0;
  min-height: 92vh;
  width: 100%;
  margin: 0 auto;
}

#Comment h2 {
  color: #fff;
  font-size: 30px;
}

.Comment-rows {
  position: relative;
  width: 100%;
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(161px, 1fr));
  opacity: 0;
  transform: translateY(20px);
  transition: opacity 400ms ease-in, transform 300ms ease-in;
}

.Comment-rows.appear {
  opacity: 1;
  transform: translateY(0);
}

.Comment-rows .Comment-form {
  position: relative;
  width: 100%;
  padding: 0 10px;
  margin: 5rem 0;
  transition: 0.5s;
}

.Comment-rows .Comment-form .Comment-InputBox {
  position: relative;
  width: 100%;
  height: 40px;
  color: var(--yellow-primary-color);
}

.Comment-rows .Comment-form .Comment-InputBox label {
  font-weight: normal;
}

.Comment-rows .Comment-form .Comment-InputBox input,
.Comment-rows .Comment-form .Comment-InputBox.textarea textarea {
  position: absolute;
  width: 100%;
  height: 100%;
  background: transparent;
  box-shadow: none;
  border: none;
  outline: none;
  font-size: 1.8rem;
  padding: 0 10px;
  z-index: 1;
  color: var(--purple-primary-color);
}

.Comment-rows .Comment-form .Comment-InputBox .Comment-text {
  position: absolute;
  top: 0;
  right: 0;
  line-height: 40px;
  font-size: 1.8rem;
  padding: 0 10px;
  display: block;
  transition: 0.5s;
  pointer-events: none;
}

.Comment-rows
  .Comment-form
  .Comment-InputBox
  input:not([required]):not(:placeholder-shown)
  + .Comment-text,
.Comment-rows .Comment-form .Comment-InputBox input:focus + .Comment-text,
.Comment-rows
  .Comment-form
  .Comment-InputBox
  input[required]:valid
  + .Comment-text,
.Comment-rows
  .Comment-form
  .Comment-InputBox
  input[type="email"]:not(:placeholder-shown)
  + .Comment-text {
  top: -35px;
  right: -10px;
}

.Comment-rows .Comment-form .Comment-InputBox .Comment-Line {
  position: absolute;
  bottom: 0;
  display: block;
  width: 100%;
  height: 2px;
  background: var(--yellow-primary-color);
  transition: 0.5s;
  border-radius: 2px;
  pointer-events: none;
}

.Comment-rows
  .Comment-form
  .Comment-InputBox
  input:not([required]):not(:placeholder-shown)
  ~ .Comment-Line,
.Comment-rows .Comment-form .Comment-InputBox input:focus ~ .Comment-Line,
.Comment-rows
  .Comment-form
  .Comment-InputBox
  input[required]:valid
  ~ .Comment-Line,
.Comment-rows
  .Comment-form
  .Comment-InputBox
  input[type="email"]:not(:placeholder-shown)
  ~ .Comment-Line {
  height: 100%;
}

/*textarea*/
.Comment-rows .Comment-form .Comment-InputBox.textarea {
  width: 100%;
  height: 100px;
  position: relative;
  padding: 10px 0;
}

.Comment-rows .Comment-form .Comment-InputBox.textarea textarea {
  height: 100%;
  resize: none;
  top: 0;
  right: 0;
}

.Comment-rows .Comment-form .Comment-InputBox textarea:focus + .Comment-text,
.Comment-rows .Comment-form .Comment-InputBox textarea:valid + .Comment-text {
  top: -35px;
  right: -10px;
}

.Comment-rows .Comment-form .Comment-InputBox textarea:focus ~ .Comment-Line,
.Comment-rows .Comment-form .Comment-InputBox textarea:valid ~ .Comment-Line {
  height: 100%;
}

/*send button*/
.Comment-form-Button {
  display: flex;
  justify-content: center;
  align-items: center;
  margin: 6rem auto 0;
}

.Comment-form-Button button {
  position: relative;
  text-decoration: none;
  width: 200px;
  padding: 2rem 5rem;
  display: block;
  text-align: center;
  overflow: hidden;
  border-radius: 3px;
  border: none;
  outline: none;
  background: none;
}

.Comment-form-Button button:hover .liquid,
.Comment-form-Button button:focus .liquid {
  top: -120px;
}

.Comment-form-Button button span {
  font-size: 1.5rem;
  position: relative;
  z-index: 1;
  color: #fff;
}

.Comment-form-Button button .liquid {
  position: absolute;
  width: 200px;
  height: 200px;
  left: 0;
  top: -80px;
  background: #0039fa;
  box-shadow: inset 0 0 50px rgba(0, 0, 0, 0.5);
  transition: 0.5s;
}

.Comment-form-Button button .liquid::before,
.Comment-form-Button button .liquid::after {
  position: absolute;
  content: "";
  width: 200%;
  height: 200%;
  top: 0;
  left: 50%;
  transform: translate(-50%, -75%);
}

.Comment-form-Button button .liquid::before {
  border-radius: 45%;
  background: rgba(20, 20, 20, 1);
  animation: CommentButton 5s linear infinite;
}

.Comment-form-Button button .liquid::after {
  border-radius: 40%;
  background: rgba(20, 20, 20, 0.5);
  animation: CommentButton 10s linear infinite;
}

@media (max-width: 210px) {
  .Comment-form-Button button {
    margin: auto 10px;
  }
}

@keyframes CommentButton {
  0% {
    transform: translate(-50%, -75%) rotate(0deg);
  }

  100% {
    transform: translate(-50%, -75%) rotate(360deg);
  }
}

