@media (max-width: 410px) {
  .Matin-Picture {
    width: 100%;
  }

  .non-profile img {
    display: none;
  }

  .profile-name {
    margin: 70px auto 0;
  }

  .profile {
    margin: -100px auto 0;
  }

  #Matin-Pic button {
    margin: 0 10px 10px;
  }

  #entrepreneur-quotes {
    margin: 2rem !important;
    padding: 3rem 0.5rem;
  }

  #entrepreneur-quotes-container .cards .content {
    font-size: 1.5rem;
  }

  #entrepreneur-quotes-container .cards .overlay:hover ~ .content {
