@media (max-width: 316px) {
  .section-1 h1 {
    font-size: 1.6rem !important;
  }

  .section-1 p {
    font-size: 1.3rem;
    line-height: 2;
  }
}

@media (max-width: 265px) {
  .section-1 h1 {
    font-size: 1.2rem !important;
  }

  .section-1 p {
    font-size: 1.1rem;
    line-height: 1.5;
  }

  #logo-header {
    display: none;
  }

  #Matin-Pic {
    display: none;
  }

  #entrepreneur-quotes {
    display: none;
  }

  .Contact-info:nth-child(1) span {
    word-break: break-word;
    bottom: 10px;
  }
}

@media (max-width: 450px) {
  #MyPicLarge .modal-content {
    height: 471px;
    width: 280px;
    margin: 5rem auto 0;
    display: block;
    text-align: center;
    border-radius: 10px;
  }

  #MyPicLarge img {
    height: 325px;
    width: 240px;
  }

  .section-1 h1 {
    font-size: 2rem;
  }

  .section-1 p {
    font-size: 1.4rem;
    line-height: 2.2;
  }

  #MyPicLarge h4 {
    font-weight: normal;
    font-size: 1.2rem;
  }
}

@media (max-width: 300px) {
  #MyPicLarge .modal-content {
    display: none;
  }

  #Matin-Pic a[href*="MyPicLarge"] {
    pointer-events: none;
  }

  .Comment-rows .Comment-form .Comment-InputBox .Comment-Line {
    width: 100%;
  }
}

