/* Modal Styles */
.threejs-modal {
  display: none;
  position: fixed;
  z-index: 2;
  left: 0;
  top: 0;
  width: 100%;
  height: 100%;
  overflow: auto;
  background-color: rgba(0, 0, 0, 0.5);
}

.threejs-modal-content {
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  background: linear-gradient(to right, #ffde5d, var(--yellow-primary-color));
  padding: 20px;
  border: 1px solid #888;
  border-radius: 10px;
  max-width: 80%;
  max-height: 80%;
  overflow: auto;
  text-align: left;
  direction: ltr;
}

.threejs-modal-content__upper {
  display: flex;
  justify-content: space-evenly;
  align-items: center;
}

.threejs-modal-content h2 {
  text-align: center;
}

.threejs-modal-content img {
  max-width: 100%;
  width: 150px;
  height: auto;
  border-radius: 50%;
}

.threejs-modal-content-text {
  background: linear-gradient(to right, rgb(140, 23, 113), rgb(70, 17, 83));
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  margin: 3rem auto;
}

.threejs-close {
  color: #aaa;
  float: right;
  font-size: 28px;
  font-weight: bold;
}

.threejs-close:hover,
.threejs-close:focus {
  color: black;
  text-decoration: none;
  cursor: pointer;
}

#threejs-btn {
  background-color: var(--purple-primary-color);
  white-space: nowrap;
  overflow: hidden;
  text-overflow: ellipsis;
  text-decoration: none;
  color: white;
  padding: 10px 20px;
  border: none;
  border-radius: 5px;
  cursor: pointer;
  display: block;
  text-align: center;
  width: 100%;
  margin: 0 auto;
  transition: 0.2s all ease-in;
}

#threejs-btn:hover {
  background-color: var(--blue-light-color);
  color: var(--purple-primary-color);
}

/* Show modal */
.modal.show {
  display: block;
}

/* Styles for the fixed trigger modal */
#fixed-trigger-modal {
  display: none;
  padding: 20px 5px;
  position: fixed;
  top: 34%;
  right: 0;
  width: 100px;
  height: 140px;
  background-color: var(--purple-primary-color);
  border-radius: 10px 0px 0px 10px;
  cursor: pointer;
  z-index: 9999;
  transition: 0.2s all ease-in;
}

#fixed-trigger-modal:hover {
  background: var(--blue-light-color);
}

#fixed-trigger-modal::before {
  content: "3D Portfolio";
