  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  color: white;
  font-size: 14px;
  text-align: center;
}

#fixed-trigger-modal:hover::before {
  color: var(--purple-primary-color);
}

.gradient-3d-text {
  background: linear-gradient(
    to right,
    var(--dark-primary-color),
    var(--purple-primary-color)
  );
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
}

