/*═════════════════════╣ Start of Contact ╠═════════════════════*/
/*⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜*/

#Contact {
  /* background: #fff; */
  width: 100%;
  height: auto;
  margin: 0 auto 50px;
}

.Contact-info {
  width: 100%;
  display: flex;
  flex-wrap: wrap;
  justify-content: center;
  margin: 60px auto 70px;
}

.cards-Contact {
  position: relative;
  flex: 1;
  max-width: 300px;
  height: 140px;
  background-color: var(--purple-primary-color);
  justify-content: center;
  align-items: center;
  cursor: pointer;
  margin: 20px;
  display: flex;
  border-radius: 5px;
  opacity: 0;
  transition: opacity 400ms ease-in, transform 500ms ease-in;
  transform: translateY(-50px);
}

.cards-Contact.appear {
  transform: translate(0);
  opacity: 1;
}

.icon-Contact {
  font-size: 3rem;
  color: var(--blue-light-color);
  transition: 0.3s linear;
}

.cards-Contact:hover .icon-Contact {
  transform: scale(4);
  opacity: 0;
}

.card-content h3,
.card-content span {
  position: absolute;
  left: 50%;
  transform: translate(-50%);
  font-size: 1.4rem;
  direction: ltr;
  opacity: 0;
}

.card-content h3 {
  color: var(--blue-light-color);
  top: 0;
}

.card-content span {
  bottom: 10px;
  color: var(--yellow-primary-color);
  font-weight: 500;
}

.cards-Contact:hover h3 {
  opacity: 1;
  top: 25px;
  transition: 0.3s linear 0.3s;
}

.cards-Contact:hover span {
  opacity: 1;
  bottom: 45px;
  transition: 0.3s linear 0.3s;
}

/*Down Contact*/
.Down-Contact {
  height: 100%;
  width: 100%;
  display: flex;
  place-items: center;
  place-content: center;
  margin: 20px auto;
}

.Down-Contact .Down-Contact-Button {
  display: inline-block;
  width: 60px;
  height: 60px;
  margin: 0 30px;
  overflow: hidden;
  background-color: var(--yellow-primary-color);
  border-radius: 50px;
  cursor: pointer;
  box-shadow: 0 10px 10px rgba(0, 0, 0, 0.1);
  transition: all 0.3s ease-out, opacity 300ms ease-out,
    transform 400ms ease-out;
  direction: ltr;
  opacity: 0;
}

.Down-Contact .Down-Contact-Button a {
  text-decoration: none;
  color: #000;
}

.Down-Contact .Down-Contact-Button:nth-child(odd) {
  transform: translateY(-20%);
}

.Down-Contact .Down-Contact-Button:nth-child(even) {
  transform: translateY(20%);
}

.Down-Contact .Down-Contact-Button.appear {
  transform: translate(0);
  opacity: 1;
}

.Down-Contact .Down-Contact-Button:hover {
  width: 200px;
}

.Down-Contact .Down-Contact-Button .Down-Contact-Icon {
  display: inline-block;
  height: 60px;
  width: 60px;
  border-radius: 50px;
  box-sizing: border-box;
  line-height: 60px;
  text-align: center;
  transition: 0.3s all ease-out;
}

.Down-Contact .Down-Contact-Button:nth-child(1):hover .Down-Contact-Icon {
  background: rgb(255, 0, 191);
}

.Down-Contact .Down-Contact-Button:nth-child(2):hover .Down-Contact-Icon {
  background: rgb(86, 154, 243);
}

.Down-Contact .Down-Contact-Button:nth-child(3):hover .Down-Contact-Icon {
  background: rgb(255, 123, 0);
}

.Down-Contact .Down-Contact-Button:nth-child(4):hover .Down-Contact-Icon {
  background: rgb(0, 0, 204);
}

.Down-Contact .Down-Contact-Button:nth-child(5):hover .Down-Contact-Icon {
  background: rgb(37, 211, 102);
}

.Down-Contact .Down-Contact-Button:nth-child(6):hover .Down-Contact-Icon {
  background: #0088cc;
}

.Down-Contact .Down-Contact-Button:nth-child(7):hover .Down-Contact-Icon {
  background: #316ff6;
}

.Down-Contact .Down-Contact-Button .Down-Contact-Icon i {
  font-size: 3rem;
  line-height: 60px;
}

.Down-Contact .Down-Contact-Button:hover .Down-Contact-Icon i {
  color: #fff !important;
}

.Down-Contact .Down-Contact-Button span {
  font-size: 2.5rem;
  line-height: 60px;
  margin-left: 10px;
  font-weight: 500;
  transition: all 0.3s ease-out;
}

.Down-Contact .Down-Contact-Button:nth-child(1) span {
  color: rgb(255, 0, 191);
}

.Down-Contact .Down-Contact-Button:nth-child(2) span {
  color: rgb(86, 154, 243);
}

.Down-Contact .Down-Contact-Button:nth-child(3) span {
  color: rgb(255, 123, 0);
}

.Down-Contact .Down-Contact-Button:nth-child(4) span {
  color: rgb(0, 0, 204);
}

.Down-Contact .Down-Contact-Button:nth-child(5) span {
  color: rgb(37, 211, 102);
}

.Down-Contact .Down-Contact-Button:nth-child(6) span {
  color: #0088cc;
}

.Down-Contact .Down-Contact-Button:nth-child(7) span {
  color: #316ff6;
}

/*the text*/
#Contact p {
  text-align: center;
  font-size: 1.7rem;
  font-weight: 500;
  margin: 80px auto 47px;
  max-width: 83%;
  opacity: 0;
  transform: translateY(-20%);
  transition: opacity 350ms ease, transform 300ms ease;
}

#Contact p.appear {
  opacity: 1;
  transform: translateY(0);
}

@media (max-width: 992px) {
  .cards-Contact {
    flex: 100%;
    max-width: 500px;
  }

  .Contact-info {
    margin: 0 auto;
  }

  .LastChild-Cards-Contact {
    margin-bottom: 20px;
  }

  #AboutMe-image img:nth-child(1),
  #AboutMe-image img:nth-child(2) {
    width: 100%;
