footer {
  min-height: 25vh;
  width: 100%;
  text-align: center;
}

.footer-icons {
  list-style: none;
  margin: 0 20px;
  padding: 1.5rem 0;
}

.footer-icons li {
  width: 70px;
  height: 70px;
  position: relative;
  display: inline-block;
  margin: 10px;
  cursor: pointer;
}

.footer-icons li::before {
  content: "";
  position: absolute;
  width: 100%;
  height: 100%;
  top: 0;
  left: 0;
  background: linear-gradient(45deg, var(--purple-primary-color), #6658a7);
  z-index: -1;
  border-radius: 10%;
  transition: all 0.3s ease-in;
}

.footer-icons li:hover::before {
  transform: rotate(360deg);
  border-radius: 100%;
}

.footer-icons li a span {
  color: #fff;
  line-height: 70px;
  font-size: 27px;
  transition: all 0.3s ease-out;
}

.footer-icons li:hover a span {
