.loader-container {
    background: rgba(0, 0, 0, .5);
    z-index: 99999;
    display: flex;
    justify-content: center;
    align-items: center;
    position: relative;
    opacity: 1;
    transition: opacity .5s ease;
    width: 100%;
    height: 100%;
    top: 0;
    left: 0;
    position: fixed;
    visibility: visible;
}

.loader-container.hidden {
    opacity: 0;
    visibility: hidden;
    transition: opacity 0.5s ease, visibility 0s 0.5s;
}

.loader {
    width: 100px;
    aspect-ratio: 1;
    padding: 1rem;
    box-sizing: border-box;
    display: grid;
    background: #fff;
    filter: blur(5px) contrast(10) hue-rotate(180deg);
    mix-blend-mode: darken;
    position: fixed;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
    border-radius: 1rem;
}

.loader:before,
.loader:after {
    content: "";
    grid-area: 1/1;
    margin: 2.9rem 0;
    border-radius: 100px;
    background: green;
    animation: l4 2s infinite linear;
}

.loader:after {
    --s: -1;
}

@keyframes l4 {
    100% {
        transform: rotate(calc(var(--s, 1)*1turn))
    }
}

/*⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜*/
