/*═════════════════════╣ Start of About Me ╠═════════════════════*/
/*⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜*/

#Matin-Pic {
    background: var(--purple-secondary-color);
    animation: MyPic-load ease-in 300ms var(--MyPic-delay-time);
    animation-fill-mode: forwards;
    opacity: 0;
    transform: translate(0, 0);
    min-height: 71vh;
}

.Matin-Picture {
    user-select: none;
    margin: 15px auto;
    color: #ffffff;
    width: 380px;
    background: var(--purple-primary-color);
    text-align: center;
    border-radius: 5px;
    box-shadow: 0 10px 20px -10px rgba(0, 0, 0, 0.75);
    animation: float-lg-cardProfile 35s ease-in-out infinite;
}

.cover-photo {
    background: var(--blue-light-color);
    height: 130px;
    width: 100%;
    border-radius: 5px 5px 0 0;
}

.non-profile {
    height: 130px;
    width: 100%;
}

.profile {
    width: 100%;
    max-width: 127px;
    height: auto;
    object-fit: cover;
    border-radius: 50%;
    border: 1px solid var(--purple-tertiary-color);
    padding: 7px;
    background: #292343;
    margin: -100px 0 0 203px;
    transition: .4s ease-in-out;
}

.profile:hover {
    cursor: pointer;
    transform: scale(1.2);
}

.profile-name {
    font-size: 25px;
    font-weight: bold;
    margin: 25px 0 0 -100px;
}

.about {
    margin-top: 35px;
    line-height: 25px;
    font-size: 1.7rem;
}

#Matin-Pic button {
    margin: 15px 10px 30px;
}

.msg-btn,
.follow-btn {
    border-radius: 3px;
    cursor: pointer;
    background: var(--blue-light-color);
    color: var(--purple-primary-color);
    border: 1px solid var(--blue-light-color);
    padding: 10px 25px;
}

.follow-btn {
    transition: .5s;
    background: transparent;
    color: var(--blue-light-color);
}

.follow-btn:hover {
    color: var(--purple-primary-color);
    background: var(--blue-light-color);
}

.msg-btn {
    transition: .5s;
}

.msg-btn:hover {
    background: var(--purple-primary-color);
    color: var(--blue-light-color);
}

#Matin-Pic i {
    font-size: 20px;
    padding-left: 20px;
    margin-bottom: 20px;
    transition: .5s;
    cursor: pointer;
    color: var(--blue-light-color);
}

#MyPicLarge .modal-content {
    height: 570px;
    width: 418px;
    margin: 0 auto;
    display: block;
    text-align: center;
    border-radius: 10px;
}

#MyPicLarge h4 {
    color: #000;
    font-weight: 500;
}

#MyPicLarge .modal-header {
    background: var(--blue-light-color);
    border-radius: 10px 10px 0 0;
}

#MyPicLarge .close {
    color: red;
}

#MyPicLarge img {
    height: 472px;
    width: 354px;
    border-radius: 7px;
    border: 3px solid var(--purple-tertiary-color);
    padding: 10px;
    background: rgba(0, 0, 0, .9);
}

#MyPicLarge .modal-footer {
    background: var(--blue-light-color);
    text-align: center;
    border-radius: 0 0 10px 10px;
}

#MyPicLarge .modal-body {
    background: rgba(168, 198, 222, .3);
}

.section-1 {
    height: auto;
    width: 100%;
}

.section-1 img {
    margin-top: 160px;
    border-radius: 10px;
    width: 100%;
    height: auto;
}

#AboutMe-image img:nth-child(1) {
    box-shadow: -5px -5px 10px blue;
    display: block;
    animation: float-lg 6s ease-in-out infinite;
    width: 100%;
    max-width: 534px;
    height: auto;
    object-fit: contain;
}

#AboutMe-image img:nth-child(2) {
    display: none;
    box-shadow: -5px 5px 10px blue;
    width: 100%;
    max-width: 283px;
    height: auto;
    object-fit: contain;
}

.section-1-image img {
    display: none;
    box-shadow: 5px 5px 10px blue;
}

.section-1 h1 {
    font-style: oblique;
    font-weight: bold;
    font-size: 3rem;
    margin: 60px auto;
    color: blue;
}

.section-1 h1 span {
    font-weight: bold;
}

.section-1 p {
    color: var(--dark-primary-color);
}

.section-1 p:nth-child(2) {
    margin: 0 auto 15px;
    font-size: 1.6rem;
    text-align: center;
}

.section-1 p:nth-child(3) {
    font-size: 1.4rem;
    line-height: 2.5;
}

#AboutMe {
    margin-bottom: 110px;
    min-height: 78vh;
    display: grid;
}

#AboutMe-image {
    transform: translateY(10%);
}

#AboutMe-Text {
    padding: 0 5rem 0 8rem;
    transform: translateX(5%);
}

#AboutMe-Text,
#AboutMe-image {
    transition: opacity 400ms ease-in, transform 400ms ease-in;
    opacity: 0;
}

#AboutMe-Text.appear,
#AboutMe-image.appear {
    transform: translateY(0);
    opacity: 1;
}

/* download resume button */

.download-resume {
    display: flex;
    justify-content: center;
    align-items: center;
}

.btn-download {
    position: relative;
    padding: 1.5rem 3rem;
    font-size: 1.8rem;
    font-weight: bold;
    color: var(--blue-light-color);
    background: var(--purple-primary-color);
    border-radius: 50px;
    overflow: hidden;
    text-decoration: none !important;
    transition: all 0.4s ease;
}

.btn-download i {
    margin-left: 10px;
    transition: transform 0.4s ease;
}

.btn-download::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    border-radius: 50px;
    transition: all 0.4s ease;
    transform: scale(1.1);
    z-index: 0;
}

.btn-download:hover {
    color: var(--purple-primary-color);
    background: var(--blue-light-color);
    box-shadow: 0 10px 20px rgba(0, 0, 0, 0.2);
}

.btn-download:focus {
    color: var(--purple-primary-color);
    background: var(--blue-light-color);
}

.btn-download:hover i {
    transform: translateX(3px);
}

.btn-download:hover::before {
    transform: scale(1.3);
}

@media (max-width: 768px) {
    .section-1-image img {
        display: block;
        max-width: 100%;
        height: auto;
        margin: 20px auto;
    }

    #AboutMe-image img:nth-child(1) {
        display: none;
    }

    #AboutMe-image img:nth-child(2) {
        display: none;
    }

    .Matin-Picture {
        animation: none;
    }

    .header-p {
        display: none;
    }

    #Home .col-sm-4 {
        float: none;
    }

    .Down-Contact .Down-Contact-Button {
        margin: 0 auto;
    }

    .section-2-right ul li:first-child {
        margin-top: 55px;
    }

    .threejs-modal-content__upper h2 {
        font-size: 2rem;
    }

    #welcome-overlay {
        gap: 2rem;
    }

    #welcome-overlay h3 {
        font-size: 3rem;
        border-bottom: 3px solid var(--purple-primary-color);
    }

    #welcome-overlay p {
        font-size: 2rem;
        line-height: 3.5rem;
    }
}

@media (min-width:768px) and (max-width:1024px) {
    .Matin-Picture {
        animation: float-md-cardProfile 10s ease-in-out infinite;
    }
}

@media (max-width: 410px) {
    .Matin-Picture {
        width: 100%;
    }

    .non-profile img {
        display: none;
    }

    .profile-name {
        margin: 70px auto 0;
    }

    .profile {
        margin: -100px auto 0;
    }

    #Matin-Pic button {
        margin: 0 10px 10px;
    }

    #entrepreneur-quotes {
        margin: 2rem !important;
        padding: 3rem .5rem;
    }

    #entrepreneur-quotes-container .cards .content {
        font-size: 1.5rem;
    }

    #entrepreneur-quotes-container .cards .overlay:hover~.content {
        transition-delay: 0.5s;
    }

    .profile:hover {
        transform: scale(1);
        cursor: pointer;
    }

    .profile {
        width: 125px;
    }
}

@media (max-width: 316px) {
    .section-1 h1 {
        font-size: 1.6rem !important;
    }

    .section-1 p {
        font-size: 1.3rem;
        line-height: 2;
    }
}

@media (max-width: 265px) {
    .section-1 h1 {
        font-size: 1.2rem !important;
    }

    .section-1 p {
        font-size: 1.1rem;
        line-height: 1.5;
    }

    #logo-header {
        display: none;
    }

    #Matin-Pic {
        display: none;
    }

    #entrepreneur-quotes {
        display: none;
    }

    .Contact-info:nth-child(1) span {
        word-break: break-word;
        bottom: 10px;
    }
}

@media (max-width: 450px) {
    #MyPicLarge .modal-content {
        height: 471px;
        width: 280px;
        margin: 5rem auto 0;
        display: block;
        text-align: center;
        border-radius: 10px;
    }

    #MyPicLarge img {
        height: 325px;
        width: 240px;
    }

    .section-1 h1 {
        font-size: 2rem;
    }

    .section-1 p {
        font-size: 1.4rem;
        line-height: 2.2;
    }

    #MyPicLarge h4 {
