    }

    button#got-it-button {
        font-size: 2rem;
    }
}

/* Modal Styles */
.threejs-modal {
    display: none;
    position: fixed;
    z-index: 2;
    left: 0;
    top: 0;
    width: 100%;
    height: 100%;
    overflow: auto;
    background-color: rgba(0, 0, 0, 0.5);
}

.threejs-modal-content {
    position: absolute;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
    background: linear-gradient(to right, #ffde5d, var(--yellow-primary-color));
    padding: 20px;
    border: 1px solid #888;
    border-radius: 10px;
    max-width: 80%;
    max-height: 80%;
    overflow: auto;
    text-align: left;
    direction: ltr;
}

.threejs-modal-content__upper {
    display: flex;
    justify-content: space-evenly;
    align-items: center;
}

.threejs-modal-content h2 {
    text-align: center;
}

.threejs-modal-content img {
    max-width: 100%;
    width: 150px;
    height: auto;
    border-radius: 50%;
}

.threejs-modal-content-text {
    background: linear-gradient(to right, rgb(140, 23, 113), rgb(70, 17, 83));
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    margin: 3rem auto;
}

.threejs-close {
    color: #aaa;
    float: right;
    font-size: 28px;
    font-weight: bold;
}

.threejs-close:hover,
.threejs-close:focus {
    color: black;
    text-decoration: none;
    cursor: pointer;
}

#threejs-btn {
    background-color: var(--purple-primary-color);
    white-space: nowrap;
    overflow: hidden;
    text-overflow: ellipsis;
    text-decoration: none;
    color: white;
    padding: 10px 20px;
    border: none;
    border-radius: 5px;
    cursor: pointer;
    display: block;
    text-align: center;
    width: 100%;
    margin: 0 auto;
    transition: .2s all ease-in;
}

#threejs-btn:hover {
    background-color: var(--blue-light-color);
    color: var(--purple-primary-color);
}


/* Show modal */
.modal.show {
    display: block;
}

/* Styles for the fixed trigger modal */
#fixed-trigger-modal {
    display: none;
    padding: 20px 5px;
    position: fixed;
    top: 34%;
    right: 0;
    width: 100px;
    height: 140px;
    background-color: var(--purple-primary-color);
    border-radius: 10px 0px 0px 10px;
    cursor: pointer;
    z-index: 9999;
    transition: 0.2s all ease-in;
}

#fixed-trigger-modal:hover {
    background: var(--blue-light-color);

}

#fixed-trigger-modal::before {
    content: '3D Portfolio';
    position: absolute;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
    color: white;
    font-size: 14px;
    text-align: center;

}

#fixed-trigger-modal:hover::before {
    color: var(--purple-primary-color);

}

.gradient-3d-text {
    background: linear-gradient(to right, var(--dark-primary-color), var(--purple-primary-color));
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
}

/*⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜*/
/*═════════════════════╣ End of Resume ╠═════════════════════*/
/*⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜*/

/*⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜*/
/*═════════════════════╣ Start of Entrepreneur ╠═════════════════════*/
/*⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜*/

#entrepreneur-quotes {
    background: rgba(168, 198, 222, .2);
    border-radius: 15px 300px;
    padding: 3rem 0;
    margin: 2rem 5rem;
}

#entrepreneur-quotes-container {
    width: 100%;
    display: flex;
    justify-content: space-between;
    position: relative;
}

#entrepreneur-quotes-container .cards {
    width: 265px;
    height: 400px;
    position: relative;
    background: #000;
    display: flex;
    justify-content: center;
    align-items: center;
    border-radius: 10%;
    margin: 40px auto;
    transform: translateY(25%);
    opacity: 0;
    transition: opacity 500ms ease-in, transform 400ms ease-in;
}

#entrepreneur-quotes-container .cards.appear {
    transform: translateY(0);
    opacity: 1;
    transition: transform 300ms ease-in-out;
}

#entrepreneur-quotes-container .cards.appear:hover {
    transform: translateX(4px) skew(0.5deg, -0.4deg);
}

#entrepreneur-quotes-container .cards::before {
    content: '';
    width: 50%;
    height: 100%;
    left: 0;
    top: 0;
    position: absolute;
    pointer-events: none;
}

.quotes-test:nth-child(2) .cards,
.quotes-test:nth-child(2) .cards .overlay {
    background: linear-gradient(45deg, #3503ad, #f7308c);
    box-shadow: 0px 0px 7px 4px rgba(0, 0, 0, .5);

}

.quotes-test:nth-child(3) .cards,
.quotes-test:nth-child(3) .cards .overlay {
    background: linear-gradient(45deg, #ccff00, #09afff);
    box-shadow: 0px 0px 15px 7px rgba(0, 0, 0, .8);
}

.quotes-test:nth-child(4) .cards,
.quotes-test:nth-child(4) .cards .overlay {
    background: linear-gradient(45deg, #e91e63, #ffeb3b);
    box-shadow: 0px 0px 7px 4px rgba(0, 0, 0, .5);
}

#entrepreneur-quotes-container .cards .imgBx {
    width: 100%;
    height: 100%;
    display: flex;
    justify-content: center;
    align-items: center;
    border-radius: 10%;
    overflow: hidden;
    position: absolute;
    transform: skew(10px, 10px);
}

.quotes-test h3 {
    padding: 0;
    letter-spacing: 2px;
    font-weight: 700;
    text-transform: uppercase;
    font-size: 2rem;
    text-align: center;
    -webkit-text-stroke: 1px black;
    color: var(--yellow-primary-color);
    font-family: Cursive;
    text-shadow: 4px 4px 5px #D4AF37;
}

#entrepreneur-quotes-container .cards .overlay {
    width: 50px;
    height: 50px;
    display: flex;
    position: absolute;
    right: 20px;
    bottom: 20px;
    border-radius: 50%;
    transition: .5s;
    cursor: pointer;
    box-shadow: 0 2px 5px rgba(0, 0, 0, 0.2);
}

#entrepreneur-quotes-container .cards .overlay:hover {
    width: 100%;
    height: 100%;
    right: 0;
    bottom: 0;
    box-shadow: none;
    opacity: .9;
    border-radius: 10%;
}

#entrepreneur-quotes-container .cards .overlay::before {
    content: 'بخوان';
    left: 0;
    right: 0;
    top: 0;
    bottom: 0;
    font-size: 12px;
    letter-spacing: 1px;
    font-weight: 500;
    color: #fff;
    position: absolute;
    display: flex;
    justify-content: center;
    align-items: center;
    transition-delay: .5s;
}

#entrepreneur-quotes-container .cards .overlay:hover::before {
    display: none;
    transition-delay: .5s;
}

#entrepreneur-quotes-container .cards .content {
    padding: 20px;
    box-sizing: border-box;
    color: #fff;
    z-index: 1;
    pointer-events: none;
    opacity: 0;
    visibility: hidden;
    transition: 0s;
    text-align: center;
    line-height: 2;
    font-size: 1.7rem;
}

#entrepreneur-quotes-container .cards .overlay:hover~.content {
    opacity: 1;
    visibility: visible;
    transition: .2s;
    transition-delay: .5s;
}

.imgBx img {
    width: 100%;
    height: 100%;
    object-fit: cover;
    border-radius: 10%;
}

@media (max-width: 980px) {
    #entrepreneur-quotes {
        border-radius: 15px 150px;
    }
}

@media (max-width: 500px) {
    #entrepreneur-quotes {
        border-radius: 15px 75px;
    }

    #Skills {
        border-radius: 100px 15px;
    }

    .threejs-modal-content__upper h2 {
        font-size: 1.7rem;
    }
}

