/*═════════════════════╣ End of Entrepreneur ╠═════════════════════*/
/*⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜*/

/*⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜*/
/*═════════════════════╣ Start of Contact ╠═════════════════════*/
/*⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜*/

#Contact {
    /* background: #fff; */
    width: 100%;
    height: auto;
    margin: 0 auto 50px;
}

.Contact-info {
    width: 100%;
    display: flex;
    flex-wrap: wrap;
    justify-content: center;
    margin: 60px auto 70px;
}

.cards-Contact {
    position: relative;
    flex: 1;
    max-width: 300px;
    height: 140px;
    background-color: var(--purple-primary-color);
    justify-content: center;
    align-items: center;
    cursor: pointer;
    margin: 20px;
    display: flex;
    border-radius: 5px;
    opacity: 0;
    transition: opacity 400ms ease-in, transform 500ms ease-in;
    transform: translateY(-50px);
}

.cards-Contact.appear {
    transform: translate(0);
    opacity: 1;
}

.icon-Contact {
    font-size: 3rem;
    color: var(--blue-light-color);
    transition: .3s linear;
}

.cards-Contact:hover .icon-Contact {
    transform: scale(4);
    opacity: 0;
}

.card-content h3,
.card-content span {
    position: absolute;
    left: 50%;
    transform: translate(-50%);
    font-size: 1.4rem;
    direction: ltr;
    opacity: 0;
}

.card-content h3 {
    color: var(--blue-light-color);
    top: 0;
}

.card-content span {
    bottom: 10px;
    color: var(--yellow-primary-color);
    font-weight: 500;
}

.cards-Contact:hover h3 {
    opacity: 1;
    top: 25px;
    transition: .3s linear .3s;
}

.cards-Contact:hover span {
    opacity: 1;
    bottom: 45px;
    transition: .3s linear .3s;
}

/*Down Contact*/
.Down-Contact {
    height: 100%;
    width: 100%;
    display: flex;
    place-items: center;
    place-content: center;
    margin: 20px auto;
}

.Down-Contact .Down-Contact-Button {
    display: inline-block;
    width: 60px;
    height: 60px;
    margin: 0 30px;
    overflow: hidden;
    background-color: var(--yellow-primary-color);
    border-radius: 50px;
    cursor: pointer;
    box-shadow: 0 10px 10px rgba(0, 0, 0, 0.1);
    transition: all .3s ease-out, opacity 300ms ease-out, transform 400ms ease-out;
    direction: ltr;
    opacity: 0;
}

.Down-Contact .Down-Contact-Button a {
    text-decoration: none;
    color: #000;
}

.Down-Contact .Down-Contact-Button:nth-child(odd) {
    transform: translateY(-20%);
}

.Down-Contact .Down-Contact-Button:nth-child(even) {
    transform: translateY(20%);
}

.Down-Contact .Down-Contact-Button.appear {
    transform: translate(0);
    opacity: 1;
}

.Down-Contact .Down-Contact-Button:hover {
    width: 200px;
}

.Down-Contact .Down-Contact-Button .Down-Contact-Icon {
    display: inline-block;
    height: 60px;
    width: 60px;
    border-radius: 50px;
    box-sizing: border-box;
    line-height: 60px;
    text-align: center;
    transition: .3s all ease-out;
}

.Down-Contact .Down-Contact-Button:nth-child(1):hover .Down-Contact-Icon {
    background: rgb(255, 0, 191);
}

.Down-Contact .Down-Contact-Button:nth-child(2):hover .Down-Contact-Icon {
    background: rgb(86, 154, 243);
}

.Down-Contact .Down-Contact-Button:nth-child(3):hover .Down-Contact-Icon {
    background: rgb(255, 123, 0);
}

.Down-Contact .Down-Contact-Button:nth-child(4):hover .Down-Contact-Icon {
    background: rgb(0, 0, 204);
}

.Down-Contact .Down-Contact-Button:nth-child(5):hover .Down-Contact-Icon {
    background: rgb(37, 211, 102);
}

.Down-Contact .Down-Contact-Button:nth-child(6):hover .Down-Contact-Icon {
    background: #0088cc;
}

.Down-Contact .Down-Contact-Button:nth-child(7):hover .Down-Contact-Icon {
    background: #316FF6;
}

.Down-Contact .Down-Contact-Button .Down-Contact-Icon i {
    font-size: 3rem;
    line-height: 60px;
}

.Down-Contact .Down-Contact-Button:hover .Down-Contact-Icon i {
    color: #fff !important;
}

.Down-Contact .Down-Contact-Button span {
    font-size: 2.5rem;
    line-height: 60px;
    margin-left: 10px;
    font-weight: 500;
    transition: all .3s ease-out;
}

.Down-Contact .Down-Contact-Button:nth-child(1) span {
    color: rgb(255, 0, 191);
}

.Down-Contact .Down-Contact-Button:nth-child(2) span {
    color: rgb(86, 154, 243);
}

.Down-Contact .Down-Contact-Button:nth-child(3) span {
    color: rgb(255, 123, 0);
}

.Down-Contact .Down-Contact-Button:nth-child(4) span {
    color: rgb(0, 0, 204);
}

.Down-Contact .Down-Contact-Button:nth-child(5) span {
    color: rgb(37, 211, 102);
}

.Down-Contact .Down-Contact-Button:nth-child(6) span {
    color: #0088cc;
}

.Down-Contact .Down-Contact-Button:nth-child(7) span {
    color: #316FF6;
}

/*the text*/
#Contact p {
    text-align: center;
    font-size: 1.7rem;
    font-weight: 500;
    margin: 80px auto 47px;
    max-width: 83%;
    opacity: 0;
    transform: translateY(-20%);
    transition: opacity 350ms ease, transform 300ms ease;
}

#Contact p.appear {
    opacity: 1;
    transform: translateY(0);
}

@media (max-width: 992px) {
    .cards-Contact {
        flex: 100%;
        max-width: 500px;
    }

    .Contact-info {
        margin: 0 auto;
    }

    .LastChild-Cards-Contact {
        margin-bottom: 20px;
    }

    #AboutMe-image img:nth-child(2) {
        display: block;
        margin: 50px auto;
        animation: float-md-2 5s ease-in-out infinite;
    }

    #AboutMe-image img:nth-child(1) {
        margin-top: 160px;
        animation: float-md-1 5s ease-in-out infinite;
    }
}

@media (max-width: 310px) {
    .Contact-info:nth-child(1) span {
        font-size: 1.2rem;
    }

    .icons {
        display: flex;
        flex-direction: column;
    }
}

@media (max-width: 520px) {
    .Down-Contact {
        display: grid;
    }

    .Down-Contact .Down-Contact-Button {
        margin: 5px auto;
    }
}

/*⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜*/
/*═════════════════════╣ End of Contact ╠═════════════════════*/
/*⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜*/

/*⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜*/
/*═════════════════════╣ Start of Comment ╠═════════════════════*/
/*⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜⚜*/

#Comment {
    background: var(--purple-primary-color);
    padding: 5rem 0;
    min-height: 92vh;
    width: 100%;
    margin: 0 auto;
}

#Comment h2 {
    color: #fff;
    font-size: 30px;
}

.Comment-rows {
    position: relative;
    width: 100%;
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(161px, 1fr));
    opacity: 0;
    transform: translateY(20px);
    transition: opacity 400ms ease-in, transform 300ms ease-in;
}

.Comment-rows.appear {
    opacity: 1;
    transform: translateY(0);
}

.Comment-rows .Comment-form {
    position: relative;
    width: 100%;
    padding: 0 10px;
    margin: 5rem 0;
    transition: .5s;
}

.Comment-rows .Comment-form .Comment-InputBox {
    position: relative;
    width: 100%;
    height: 40px;
    color: var(--yellow-primary-color);
}

.Comment-rows .Comment-form .Comment-InputBox label {
    font-weight: normal;
}

.Comment-rows .Comment-form .Comment-InputBox input,
.Comment-rows .Comment-form .Comment-InputBox.textarea textarea {
    position: absolute;
    width: 100%;
    height: 100%;
    background: transparent;
    box-shadow: none;
    border: none;
    outline: none;
    font-size: 1.8rem;
    padding: 0 10px;
    z-index: 1;
    color: var(--purple-primary-color);
}

.Comment-rows .Comment-form .Comment-InputBox .Comment-text {
    position: absolute;
    top: 0;
    right: 0;
    line-height: 40px;
    font-size: 1.8rem;
    padding: 0 10px;
    display: block;
    transition: .5s;
    pointer-events: none;
}

.Comment-rows .Comment-form .Comment-InputBox input:not([required]):not(:placeholder-shown)+.Comment-text,
.Comment-rows .Comment-form .Comment-InputBox input:focus+.Comment-text,
.Comment-rows .Comment-form .Comment-InputBox input[required]:valid+.Comment-text,
.Comment-rows .Comment-form .Comment-InputBox input[type=email]:not(:placeholder-shown)+.Comment-text {
    top: -35px;
    right: -10px;
}


.Comment-rows .Comment-form .Comment-InputBox .Comment-Line {
    position: absolute;
    bottom: 0;
    display: block;
    width: 100%;
    height: 2px;
    background: var(--yellow-primary-color);
    transition: .5s;
    border-radius: 2px;
    pointer-events: none;
}

.Comment-rows .Comment-form .Comment-InputBox input:not([required]):not(:placeholder-shown)~.Comment-Line,
.Comment-rows .Comment-form .Comment-InputBox input:focus~.Comment-Line,
.Comment-rows .Comment-form .Comment-InputBox input[required]:valid~.Comment-Line,
.Comment-rows .Comment-form .Comment-InputBox input[type=email]:not(:placeholder-shown)~.Comment-Line {
    height: 100%;
}

/*textarea*/
.Comment-rows .Comment-form .Comment-InputBox.textarea {
    width: 100%;
    height: 100px;
    position: relative;
    padding: 10px 0;
}

.Comment-rows .Comment-form .Comment-InputBox.textarea textarea {
    height: 100%;
    resize: none;
    top: 0;
    right: 0;
}

.Comment-rows .Comment-form .Comment-InputBox textarea:focus+.Comment-text,
.Comment-rows .Comment-form .Comment-InputBox textarea:valid+.Comment-text {
    top: -35px;
    right: -10px;
}

.Comment-rows .Comment-form .Comment-InputBox textarea:focus~.Comment-Line,
.Comment-rows .Comment-form .Comment-InputBox textarea:valid~.Comment-Line {
    height: 100%;
}

/*send button*/
.Comment-form-Button {
    display: flex;
    justify-content: center;
    align-items: center;
    margin: 6rem auto 0;
}

.Comment-form-Button button {
    position: relative;
    text-decoration: none;
    width: 200px;
    padding: 2rem 5rem;
    display: block;
    text-align: center;
    overflow: hidden;
    border-radius: 3px;
    border: none;
    outline: none;
    background: none;
}

.Comment-form-Button button:hover .liquid,
.Comment-form-Button button:focus .liquid {
    top: -120px;
}

.Comment-form-Button button span {
    font-size: 1.5rem;
    position: relative;
    z-index: 1;
    color: #fff;
}

.Comment-form-Button button .liquid {
    position: absolute;
    width: 200px;
    height: 200px;
    left: 0;
    top: -80px;
    background: #0039FA;
    box-shadow: inset 0 0 50px rgba(0, 0, 0, .5);
    transition: .5s;
}

.Comment-form-Button button .liquid::before,
.Comment-form-Button button .liquid::after {
    position: absolute;
    content: '';
    width: 200%;
    height: 200%;
    top: 0;
    left: 50%;
    transform: translate(-50%, -75%);
}

.Comment-form-Button button .liquid::before {
    border-radius: 45%;
    background: rgba(20, 20, 20, 1);
    animation: CommentButton 5s linear infinite;
}

.Comment-form-Button button .liquid::after {
    border-radius: 40%;
    background: rgba(20, 20, 20, .5);
    animation: CommentButton 10s linear infinite;
}

@media (max-width: 210px) {
    .Comment-form-Button button {
        margin: auto 10px;
    }
}

@keyframes CommentButton {
    0% {
        transform: translate(-50%, -75%) rotate(0deg);
    }

    100% {
        transform: translate(-50%, -75%) rotate(360deg);
    }
}

