}

#AboutMe {
    margin-bottom: 110px;
    min-height: 78vh;
    display: grid;
}

#AboutMe-image {
    transform: translateY(10%);
}

#AboutMe-Text {
    padding: 0 5rem 0 8rem;
    transform: translateX(5%);
}

#AboutMe-Text,
#AboutMe-image {
    transition: opacity 400ms ease-in, transform 400ms ease-in;
    opacity: 0;
}

#AboutMe-Text.appear,
#AboutMe-image.appear {
    transform: translateY(0);
    opacity: 1;
}

/* download resume button */

.download-resume {
    display: flex;
    justify-content: center;
    align-items: center;
}

.btn-download {
    position: relative;
    padding: 1.5rem 3rem;
    font-size: 1.8rem;
    font-weight: bold;
    color: var(--blue-light-color);
    background: var(--purple-primary-color);
    border-radius: 50px;
    overflow: hidden;
    text-decoration: none !important;
    transition: all 0.4s ease;
}

.btn-download i {
    margin-left: 10px;
    transition: transform 0.4s ease;
}

.btn-download::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    border-radius: 50px;
    transition: all 0.4s ease;
    transform: scale(1.1);
    z-index: 0;
}

.btn-download:hover {
    color: var(--purple-primary-color);
    background: var(--blue-light-color);
    box-shadow: 0 10px 20px rgba(0, 0, 0, 0.2);
}

.btn-download:focus {
    color: var(--purple-primary-color);
    background: var(--blue-light-color);
}

.btn-download:hover i {
    transform: translateX(3px);
}

.btn-download:hover::before {
    transform: scale(1.3);
}

